import { File } from '../models/file.model';
import { FileMemento } from './file-memento';
export class Originator {
  constructor(state) {
    this.state = state;
  }

  save() {
    const memento = new FileMemento(this.state);
    return memento;
  }

  restore(memento) {
    this.oldState = this.state;
    this.state = memento.getState();
  }

  redo() {
    if (this.oldState) {
      this.state = this.oldState;
      return this.save();
    }

    return null;
  }

  getState() {
    return this.state;
  }

  setState(file) {
    this.state = new File();
    this.state.path = file.path;
    this.state.data = file.data;
    this.state.type = file.type;
    this.state.configuration = file.configuration;
    this.state.saved = file.saved;
  }

}
